"use client";

import { useEffect, useRef } from "react";

export default function CanvasBackground() {
    const canvasRef = useRef<HTMLCanvasElement>(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        if (!ctx) return;

        let width: number, height: number;
        let particles: Particle[] = [];
        let animationFrameId: number;

        const config = {
            particleColor: 'rgba(212, 175, 55, 0.5)', // Accent Gold with opacity
            lineColor: 'rgba(212, 175, 55, 0.15)', // Faint Gold lines
            particleAmount: 80, // Number of particles
            defaultSpeed: 0.5,
            variantSpeed: 1,
            linkRadius: 150, // Distance to connect particles
        };

        class Particle {
            x: number;
            y: number;
            vx: number;
            vy: number;
            size: number;
            opacity: number;

            constructor() {
                this.x = Math.random() * width;
                this.y = Math.random() * height;
                this.vx = (Math.random() - 0.5) * config.defaultSpeed;
                this.vy = (Math.random() - 0.5) * config.defaultSpeed;
                this.size = Math.random() * 2 + 1; // Random size 1-3px
                this.opacity = Math.random() * 0.5 + 0.3;
            }

            update() {
                this.x += this.vx;
                this.y += this.vy;

                // Bounce off edges
                if (this.x < 0 || this.x > width) this.vx *= -1;
                if (this.y < 0 || this.y > height) this.vy *= -1;
            }

            draw() {
                if (!ctx) return;
                ctx.beginPath();
                ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
                ctx.fillStyle = config.particleColor;
                ctx.fill();
            }
        }

        const resize = () => {
            width = canvas.width = window.innerWidth;
            height = canvas.height = window.innerHeight;
        };

        const init = () => {
            resize();

            // Create particles based on screen size
            const particleCount = window.innerWidth < 768 ? 40 : config.particleAmount;
            particles = [];
            for (let i = 0; i < particleCount; i++) {
                particles.push(new Particle());
            }
        };

        const animate = () => {
            if (!ctx) return;
            ctx.clearRect(0, 0, width, height);

            // Update and draw particles
            particles.forEach((p, index) => {
                p.update();
                p.draw();

                // Draw connections
                for (let j = index + 1; j < particles.length; j++) {
                    const p2 = particles[j];
                    const distance = Math.sqrt(Math.pow(p.x - p2.x, 2) + Math.pow(p.y - p2.y, 2));

                    if (distance < config.linkRadius) {
                        ctx.beginPath();
                        ctx.strokeStyle = config.lineColor;
                        ctx.lineWidth = 1 - distance / config.linkRadius;
                        ctx.moveTo(p.x, p.y);
                        ctx.lineTo(p2.x, p2.y);
                        ctx.stroke();
                    }
                }
            });

            animationFrameId = requestAnimationFrame(animate);
        };

        window.addEventListener('resize', resize);
        init();
        animate();

        return () => {
            window.removeEventListener('resize', resize);
            cancelAnimationFrame(animationFrameId);
        };
    }, []);

    return (
        <div id="canvas-container" style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', zIndex: -1, background: '#000' }}>
            <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', background: 'linear-gradient(135deg, rgba(0, 0, 0, 0.9) 0%, rgba(0, 0, 0, 0.6) 100%)', pointerEvents: 'none' }}></div>
            <canvas ref={canvasRef} style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', zIndex: -1 }} />
        </div>
    );
}

"use client";

import { useEffect } from "react";
import { animate } from "animejs";

export default function ScrollAnimation() {
    useEffect(() => {
        // Core Reveal Function Logic
        const reveal = (selector: string, options: any = {}) => {
            const defaults = {
                duration: 1000,
                delay: 0,
                interval: 0,
                opacity: [0, 1],
                translateY: [20, 0],
                translateX: [0, 0],
                scale: [1, 1],
                easing: 'cubicBezier(0.25, 1, 0.5, 1)',
                viewport: 0.15
            };

            const config = { ...defaults, ...options };
            const elements = document.querySelectorAll(selector);

            if (elements.length === 0) return;

            const observer = new IntersectionObserver((entries) => {
                entries.forEach((entry, index) => {
                    if (entry.isIntersecting) {
                        const el = entry.target as HTMLElement;

                        const staggerDelay = config.interval ? (index * config.interval) : config.delay;

                        animate(el, {
                            opacity: config.opacity as [number, number],
                            translateY: config.translateY as [number, number],
                            translateX: config.translateX as [number, number],
                            scale: config.scale as [number, number],
                            duration: config.duration,
                            delay: staggerDelay,
                            easing: config.easing,
                            begin: () => {
                                el.style.visibility = 'visible';
                                el.classList.remove('vibe-hidden');
                            }
                        });

                        observer.unobserve(el);
                    }
                });
            }, { threshold: config.viewport });

            elements.forEach(el => {
                (el as HTMLElement).style.opacity = '0';
                observer.observe(el);
            });
        };

        // Initialize Animations
        reveal('.hero-logo-entry', { duration: 1000, translateY: [-50, 0], opacity: [0, 1], delay: 300 });
        reveal('.hero-text-entry', { duration: 1000, translateY: [50, 0], opacity: [0, 1], delay: 600 });
        reveal('.hero-tag-entry', { duration: 1000, translateY: [30, 0], opacity: [0, 1], delay: 900 });
        reveal('.hero-cta-entry', { duration: 1000, translateY: [30, 0], opacity: [0, 1], delay: 1200 });
        reveal('.hero-visual-entry', { duration: 1200, translateY: [50, 0], scale: [0.95, 1], opacity: [0, 1], delay: 1400 });
        reveal('.vibe-hidden', { duration: 1000, interval: 200, viewport: 0.15, opacity: [0, 1], translateY: [40, 0] });
        reveal('.gallery-item', { duration: 600, interval: 100, opacity: [0, 1], scale: [0.9, 1], viewport: 0.1 });

    }, []); // Run once on mount

    return null;
}
